import {
  Component,
  Input,
  OnChanges,
  OnInit,
  SimpleChanges,
} from '@angular/core';
import { NzImage, NzImageService } from 'ng-zorro-antd/image';
import { FileApiService } from 'src/app/service/api/file-api.service';

@Component({
  selector: 'app-image',
  templateUrl: './image.component.html',
  styleUrls: ['./image.component.scss'],
})
export class ImageComponent implements OnInit, OnChanges {
  @Input() fileid = '';
  @Input() width = '100px';
  @Input() height = '100px';
  @Input() cover = false;

  constructor(private api: FileApiService, private image: NzImageService) {}

  ngOnInit(): void {}

  url = '';
  fallback =
    'data:image/png;base64,';

  ngOnChanges(changes: SimpleChanges): void {
    if (this.fileid) {
      if (this.fileid.startsWith('http')) {
        this.url = this.fileid;
      } else {
        this.url = this.api.fileurl(this.fileid);
      }
    } else {
      this.url = this.fallback;
    }

    this.imgStyle = {
      width: this.width,
      height: this.height,
      'background-image': `url(${this.url})`,
    };
    if (this.cover) {
      this.imgStyle['background-size'] = 'cover';
    }
  }

  imgStyle: any = {};

  preview() {
    if (!this.url) {
      return;
    }
    const image: NzImage = {
      src: this.url,
    };
    this.image.preview([image]);
  }
}
